import create from "zustand";
import axios from "axios";
import produce from "immer";
import { INTERNAL_SERVER_ERROR } from "../../constants/strings";

const INITIAL_WALLET_STATE = {
    get: {
      loading: false,
      success: {
        ok: false,
        data: [],
      },
      failure: {
        error: false,
        message: "",
      },
    },
    post: {
      loading: false,
      success: {
        ok: false,
        data: null,
      },
      failure: {
        error: false,
        message: "",
      },
    },
  };

  const useWalletStore = create((set, address) => ({
    walletState: INITIAL_WALLET_STATE,
    getWalletAddress: async ({ shop = window.lookbook.shop, address}) => {
        set(
            produce((state) => ({
                ...state,
                walletState: {
                    ...state.walletState,
                    post:{
                        ...INITIAL_WALLET_STATE.post,
                        loading: true,
                    }
                }
            }))
        )
        try{
            console.log('Wallet Address: ', address)
            const { data } = await axios.post(`${process.env.REACT_APP_API_SHOPLOOKS_SERVER_URL}/api/post_wallet_address`, {shop, address});
            // console.log(data);
            console.log('Data fetched here', data);
        }catch(error){
            console.log(error)
        }
    }
  }))

  export default useWalletStore
