import { useEffect, useState, useContext } from "react";
import {
  Box,
  Flex,
  Stack,
  Heading,
  Text,
  Container,
  Input,
  Button,
  SimpleGrid,
  useBreakpointValue,
  FormControl,
  FormLabel,
  Icon,
  StackDivider,
  useColorModeValue,
  Image,
  useDisclosure,
  chakra,
  toast,
  useToast,
  Avatar,
  AvatarGroup,
  SkeletonText,
  SkeletonCircle,
  Skeleton,
  VStack,
  Divider,
  AvatarBadge,
  ButtonGroup,
  InputGroup,
  InputLeftAddon,
  Grid,
  GridItem,
  TableContainer,
  Table,
  Tbody,
  Tr,
  Td,
  TableCaption,
  Thead,
  Th,
  Tfoot,
  FormHelperText,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";
import { useParams, useNavigate } from "react-router-dom";
import { IoClose, IoAddOutline, IoCloseCircleOutline } from "react-icons/io5";
import { ResourcePicker } from "@shopify/app-bridge-react";
import NavBar from "../../components/navbar";

import useFilesStore from "../../store/files";
import useScriptsStore from "../../store/scripts";

import Upload from "../../components/upload";
import useLooksStore from "../../store/looks";
import { INTERNAL_SERVER_ERROR } from "../../constants/strings";
import { ShopContext } from "../../context";
import axios from "axios";

const renderSkeleton = () => {
  return (
    <Flex direction="column" width="100%">
      <Skeleton width="100%" height="40px">
        {" "}
      </Skeleton>
      <SkeletonText mt="4" noOfLines={1} spacing="4" />
      <br />
      <br />
      <Box>
        <SkeletonCircle size="20" />
        <SkeletonText mt="4" noOfLines={4} spacing="4" />
      </Box>
    </Flex>
  );
};

function CreateLooks(props) {
  const shop = useContext(ShopContext);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const {
    isOpen: isResourcePickerOpen,
    onOpen: onResourcePickerOpen,
    onClose: onResourcePickerClose,
  } = useDisclosure();
  const looks = useLooksStore((state) => state.looks);
  const files = useFilesStore((state) => state.files);
  const getLooks = useLooksStore((state) => state.getLooks);
  const postLooks = useLooksStore((state) => state.postLooks);
  const destroyLooks = useLooksStore((state) => state.destroyLooks);
  const patchLooks = useLooksStore((state) => state.patchLooks);
  const scripts = useScriptsStore((state) => state.scripts);
  const postScripts = useScriptsStore((state) => state.postScripts);
  const getScripts = useScriptsStore((state) => state.getScripts);

  const { id = "" } = useParams();
  const toast = useToast();
  const navigate = useNavigate();
  const colorMode = useColorModeValue("gray.100", "gray.700");
  const [looksName, setLooksName] = useState(props.looks.name);
  const [looksPrice, setLooksPrice] = useState(props.looks.price);
  const [uploads, setUploads] = useState(props.looks.files || []);
  const [products, setProducts] = useState(props.looks.products || []);
  const [totalProductsPrice, setTotlaProductsPrice] = useState("");
  const onUploadWidgetClose = (data = []) => {
    setUploads([...uploads, ...data]);
    onClose();
  };

  const [info, setInfo] = useState([]);
  const [input, setInput] = useState(0);
  const [from, setFrom] = useState("inr");
  const [to, setTo] = useState("xrp");
  const [output, setOutput] = useState(0);

  // console.log('XRP Price: ',output)
  console.log("Total Price: ", totalProductsPrice);
  console.log("Input Price: ", input);

  useEffect(async () => {
    await axios
      .get(
        `https://cdn.jsdelivr.net/gh/fawazahmed0/currency-api@1/latest/currencies/${from}.json`
      )
      .then((res) => {
        setInfo(res.data[from]);
      });
  }, [from]);

  const convert = () => {
    var rate = info[to];
    setOutput(input * rate);
  };

  const onResourcePickerDone = (data = {}) => {
    // console.log(data)
    setProducts([
      // ...products.filter(Boolean),
      ...data?.selection
        ?.map((d) => {
          return {
            title: d.title,
            image: (d.images[0] && d.images[0]?.originalSrc) || "",
            id: d.id,
            price: parseInt(d.variants[0]?.price) || 0,
          };
        })
        .filter(Boolean),
    ]);
    onResourcePickerClose();
    let productSum = 0;
    const result = data.selection.reduce((p, n) => {
      productSum = p + parseFloat(n.variants[0].price);
      return productSum;
    }, 0);
    setTotlaProductsPrice(result);
    convert();
    setInput(result);
  };

  const getLooskById = async () => {
    if (id) {
      const data = await getLooks({ id });
      if (data) {
        setLooksName(data?.name);
        setUploads([...uploads, ...data?.medias]);
        setLooksPrice([data?.price]);
        // console.log('asdf ', data.products)
        setProducts([
          ...products,
          ...data?.products.map((p) => ({
            id: p.admin_graphql_api_id,
            title: p.title,
            image: p?.image?.src,
            price: parseInt(p.variants[0]?.price) || 0,
          })),
        ]);
        let productSum = 0;
        const result = data.products.reduce((p, n) => {
          productSum = p + parseFloat(n.variants[0].price);
          return productSum;
        }, 0);
        setTotlaProductsPrice(result);
        convert();
        setInput(result);
      }
    }
  };

  useEffect(() => {
    getLooskById();
  }, []);

  const removeUpload = (upload, index) => {
    uploads.splice(index, 1);
    setUploads([...uploads.filter(Boolean)]);
  };

  const removeProduct = (index) => {
    products.splice(index, 1);
    setProducts([...products.filter(Boolean)]);
    // console.log(products);
    let productSum = 0;
    const result = products.reduce((p, n) => {
      productSum = p + parseFloat(n.price);
      return productSum;
    }, 0);
    setTotlaProductsPrice(result);
    convert();
    setInput(result);
  };

  const onDestroyLook = async (lookId) => {
    try {
      await destroyLooks(lookId);
      toast({
        title: `Look deleted!`,
        status: "success",
      });
      window.history.back();
    } catch (e) {
      toast({
        title: e.message || INTERNAL_SERVER_ERROR,
        status: "error",
      });
    }
  };
  const renderProducts = () => {
    return products.map((product, index) => (
      <Tr>
        <Td>
          <Image
            objectFit="contain"
            boxSize="50px"
            src={product.image}
            color={"yellow.500"}
          />
        </Td>
        <Td>{product.title}</Td>
        <Td isNumeric>{product.price}</Td>
        <Td textAlign={"center"}>
          <Icon
            as={IoClose}
            color={"red.500"}
            w={5}
            h={5}
            onClick={() => removeProduct(index)}
          />
        </Td>
      </Tr>
    ));
  };
  const renderLooks = () => {
    if (looks.get.loading) {
      return renderSkeleton();
    } else if (looks.get.failure.error) {
      return (
        <Box>
          <Flex direction="column" align="center">
            <VStack spacing="3">
              <Heading as="h1" size="md">
                {looks.get.failure.message}
              </Heading>
            </VStack>
            <br />
            <Divider />
            <br />
            <VStack spacing="3">
              <Button onClick={() => getLooskById()}>Try Again</Button>
            </VStack>
          </Flex>
        </Box>
      );
    } else {
      const { data } = looks?.get?.success;
      return (
        <>
          <Stack spacing={4}>
            <Heading
              color={"gray.800"}
              lineHeight={1.1}
              fontSize={{ base: "2xl", sm: "3xl", md: "4xl" }}
            >
              {data && data.name ? data.name : "Create a look"}
            </Heading>
            <Text color={"gray.500"} fontSize={{ base: "sm", sm: "md" }}></Text>
          </Stack>
          <Box mt={10}>
            <chakra.form
              onSubmit={async (e) => {
                e.preventDefault();
                try {
                  if (id) {
                    await patchLooks({
                      id,
                      name: looksName,
                      price: looksPrice,
                      medias: uploads,
                      products: products.map((product) => product.id),
                    });
                  } else {
                    await postLooks({
                      name: looksName,
                      price: looksPrice,
                      medias: uploads,
                      products: products.map((product) => product.id),
                    });
                    try {
                      const scriptsOnStore = await getScripts(shop);
                      if (scriptsOnStore && scriptsOnStore.length) {
                        // already has a script tag, do nothing.
                      } else {
                        await postScripts(shop);
                      }
                      window.history.back();
                    } catch (e) {
                      window.history.back();
                    }
                  }
                  toast({
                    title: `Looks ${id ? "updated" : "created"} successfully!`,
                    status: "success",
                  });
                } catch (e) {
                  toast({
                    title: e.message || INTERNAL_SERVER_ERROR,
                    status: "error",
                  });
                }
              }}
              {...props}
            >
              <Stack spacing={4}>
                <FormControl id="look-name">
                  <FormLabel>Look name</FormLabel>
                  <Input
                    placeholder="Winter Fashion Look"
                    name="look_name"
                    type="text"
                    value={looksName}
                    onChange={(e) => setLooksName(e.target.value)}
                    required
                  />
                </FormControl>

                <FormControl>
                  <FormLabel>Add medias to this look</FormLabel>
                  <AvatarGroup>
                    {uploads.map((upload, index) => (
                      <Avatar
                        key={(upload._name || upload.name) + index}
                        name={upload._name || upload.name}
                        src={upload._url || upload.url}
                        size="lg"
                        // size={useBreakpointValue({ base: 'md', md: 'lg' })}
                        position={"relative"}
                        zIndex={2}
                        _before={{
                          content: '""',
                          width: "full",
                          height: "full",
                          rounded: "full",
                          transform: "scale(1.125)",
                          bgGradient: "linear(to-bl, red.400,pink.400)",
                          position: "absolute",
                          zIndex: -1,
                          top: 0,
                          left: 0,
                        }}
                      >
                        <AvatarBadge
                          boxSize="1.25em"
                          bg="red.500"
                          onClick={() => removeUpload(upload, index)}
                        >
                          <Icon
                            as={IoCloseCircleOutline}
                            color={"white.500"}
                            w={5}
                            h={5}
                          />
                        </AvatarBadge>
                      </Avatar>
                    ))}
                    <Avatar
                      onClick={onOpen}
                      size="lg"
                      bg={"pink.400"}
                      _hover={{ bg: "pink.300" }}
                      cursor="pointer"
                      icon={<IoAddOutline size="2em" color="white" />}
                      _before={{
                        content: '""',
                        width: "full",
                        height: "full",
                        rounded: "full",
                        transform: "scale(1.2)",
                        bgGradient: "linear(to-bl, red.400,pink.400)",
                        position: "absolute",
                        zIndex: -1,
                        top: 0,
                        left: 0,
                      }}
                    ></Avatar>
                  </AvatarGroup>
                  <Upload isOpen={isOpen} onClose={onUploadWidgetClose} />
                </FormControl>
                <br />
                <br />
                <FormControl id="look-products">
                  <FormLabel>Add products for this look</FormLabel>

                  <TableContainer pb={"10px"}>
                    <Table variant="striped" colorScheme={"gray"}>
                      <Thead>
                        <Tr>
                          <Th>Product Image</Th>
                          <Th>Product Name</Th>
                          <Th isNumeric>Product Price</Th>
                          <Th>Action</Th>
                        </Tr>
                      </Thead>
                      <Tbody>
                        {renderProducts()}
                        <Tr>
                          <Td></Td>
                          <Td isNumeric fontWeight={"bold"}>
                            Total Product Price
                          </Td>
                          <Td isNumeric>
                            <Text size="14px" fontWeight={"bold"}>
                              {totalProductsPrice}
                            </Text>
                          </Td>
                          <Td></Td>
                        </Tr>
                      </Tbody>
                    </Table>
                  </TableContainer>
                  <Button
                    fontFamily={"heading"}
                    bg={"gray.200"}
                    color={"gray.800"}
                    onClick={onResourcePickerOpen}
                  >
                    Link products +
                  </Button>
                  <ResourcePicker
                    onSelection={onResourcePickerDone}
                    onCancel={onResourcePickerClose}
                    selectMultiple
                    showVariants={false}
                    resourceType="Product"
                    open={isResourcePickerOpen}
                    initialSelectionIds={products
                      .map((product) => ({ id: product.id }))
                      .filter(Boolean)}
                  />
                </FormControl>

                <Alert mt={4} status="info">
                  <AlertIcon />
                  <Grid
                    w="100%"
                    templateRows="repeat(2, 1fr)"
                    templateColumns="repeat(4, 1fr)"
                    gap={4}
                  >
                    <GridItem colSpan={4} >
                      <Text>Calculate INR to XRP</Text>
                    </GridItem>
                    <GridItem colSpan={2} >
                      <FormControl>
                        <Input type="email" />
                        <FormHelperText>
                          We'll never share your email.
                        </FormHelperText>
                      </FormControl>
                    </GridItem>
                    <GridItem colSpan={2} ></GridItem>
                  </Grid>
                </Alert>

                <FormControl id="look-price">
                  <FormLabel>
                    Look Price in XRP for the above products
                  </FormLabel>
                  <InputGroup>
                    <InputLeftAddon children="XRP" />
                    <Input
                      placeholder="100"
                      name="look_price"
                      type="text"
                      value={looksPrice}
                      onChange={(e) => setLooksPrice(e.target.value)}
                      required
                    />
                  </InputGroup>
                  <FormHelperText>
                    The Amount in XRP customers have to pay to shop all the
                    items in this look
                  </FormHelperText>
                </FormControl>
              </Stack>
              <ButtonGroup mt={8} width="full">
                {data && data.objectId ? (
                  <Button
                    isLoading={looks.destroy.loading}
                    onClick={() => onDestroyLook(data.objectId)}
                    isFullWidth
                    variant="ghost"
                    colorScheme="red"
                  >
                    Delete Look
                  </Button>
                ) : null}
                <Button
                  isLoading={
                    looks.post.loading ||
                    looks.patch.loading ||
                    scripts.get.loading ||
                    scripts.post.loading
                  }
                  disabled={
                    looks.post.loading ||
                    looks.patch.loading ||
                    scripts.get.loading ||
                    scripts.post.loading
                  }
                  loadingText={`${id ? "Updating" : "Saving"} look`}
                  type="submit"
                  fontFamily={"heading"}
                  isFullWidth
                  w={"full"}
                  bgGradient="linear(to-r, red.400,pink.400)"
                  color={"white"}
                  _hover={{
                    bgGradient: "linear(to-r, red.400,pink.400)",
                    boxShadow: "xl",
                  }}
                >
                  {`${id ? "Update" : "Save"} look`}
                </Button>
              </ButtonGroup>
            </chakra.form>
          </Box>
        </>
      );
    }
    return null;
  };

  return (
    <>
      <NavBar />
      <Box position={"relative"} bg="#ccc">
        <Container
          as={SimpleGrid}
          maxW={"7xl"}
          columns={{ base: 1, md: 1 }}
          spacing={{ base: 10, lg: 32 }}
          py={{ base: 10, sm: 15, lg: 20 }}
        >
          <Stack
            bg={"gray.50"}
            rounded={"xl"}
            p={{ base: 4, sm: 6, md: 8 }}
            spacing={{ base: 8 }}
