import {
  Box,
  Container,
  Heading,
  Table,
  TableContainer,
  Th,
  Thead,
  Tr,
  Tbody,
  Td,
  VStack,
  Text,
  Divider,
  useColorMode,
  Link,
} from "@chakra-ui/react";
import { useContext, useEffect, useState } from "react";
import NavBar from "../../components/navbar";
import { ShopContext } from "../../context";
import useTransactionStore from "../../store/transaction";

const TransactionRoute = () => {
  const shop = useContext(ShopContext);
  const transactionState = useTransactionStore(
    (state) => state.transactionState
  );
  const getTransactionState = useTransactionStore(
    (state) => state.getTransactionState
  );

  useEffect(async () => {
    getTransactionState(shop);
  }, []);


console.log(transactionState.get.success.data.transactions);
  return (
    <>

      <Container
        maxW={"7xl"}
        p={[12, 6]}
        h="100vh"
        bg="#f6f6f7"
        textAlign={"left"}
      >
        <Box bg="white" width={"5xl"} m="auto" p={5} borderRadius="10px">
          <VStack spacing={2} align="stretch">
            <Box>
              <Text size="xl" fontWeight="bold">
                HABR Transaction Details
              </Text>
              <Divider borderColor="gray.200" />
            </Box>
          </VStack>

          <TableContainer p="5">
            <Table variant={"simple"}>
              <Thead>
                <Tr>
                  <Th>Account</Th>
                  <Th isNumeric>Amount</Th>
                  <Th>Ledger Index</Th>
                  <Th>Fee</Th>
                  <Th>Transaction Ref</Th>
                </Tr>
              </Thead>
              <Tbody>
                {transactionState.get.success.data.transactions.map(
                  (details) => (
                    <Text>{details.transaction_id}</Text>
                    // <Tr>
                    //   <Td>{details.tx.Account}</Td>
                    //   <Td isNumeric>
                    //     {window.xrpl.dropsToXrp(details.tx.Amount)}
                    //   </Td>
                    //   <Td>{details.tx.inLedger}</Td>
                    //   <Td>{details.tx.Fee}</Td>
                    //   <Td>
                    //     <Link
                    //       color="teal"
                    //       target="_blank"
                    //       href={`${process.env.REACT_APP_XRP_TRANSACTION_REFFERENCE}transactions/${details.transaction_id}`}
                    //     >
                    //       {details.transaction_id}
                    //     </Link>
                    //   </Td>
                    // </Tr>
                  )
                )}
              </Tbody>
            </Table>
          </TableContainer>
        </Box>
      </Container>
    </>
  );
};

export default TransactionRoute;
