import {
  Box,
  Container,
  Heading,
  Table,
  TableContainer,
  Th,
  Thead,
  Tr,
  Tbody,
  Td,
  VStack,
  Text,
  Divider,
  useColorMode,
  Link,
} from "@chakra-ui/react";
import { useContext, useEffect, useState } from "react";
import NavBar from "../../components/navbar";
import { ShopContext } from "../../context";
import useTransactionStore from "../../store/transaction";

const TransactionRoute = () => {
  const shop = useContext(ShopContext);
  const transactionState = useTransactionStore(
    (state) => state.transactionState
  );
  const getTransactionState = useTransactionStore(
    (state) => state.getTransactionState
  );

  useEffect(async () => {
    getTransactionState(shop);
    // const url = 'https://testnet.mirrornode.hedera.com/api/v1/transactions?account.id=0.0.47870654'
    //   const result = await fetch(url)
    //   const response = await result.json();
    //   console.log(response);
  }, []);

 console.log(transactionState.get.success.data);
  return (
    <>

      <Container
        maxW={"7xl"}
        p={[12, 6]}
        h="100vh"
        bg="#f6f6f7"
        textAlign={"left"}
      >
        <Box bg="white" width={"5xl"} m="auto" p={5} borderRadius="10px">
          <VStack spacing={2} align="stretch">
            <Box>
              <Text size="xl" fontWeight="bold">
                HABR Transaction Details
              </Text>
              <Divider borderColor="gray.200" />
            </Box>
          </VStack>

          <TableContainer p="5">
            <Table variant={"simple"}>
              <Thead>
                <Tr>
                  <Th>Account</Th>
                  <Th isNumeric>Amount</Th>
                  <Th>Ledger Index</Th>
                  <Th>Fee</Th>
                  <Th>Transaction Ref</Th>
                </Tr>
              </Thead>
              <Tbody>
                {transactionState.get.success.data.transactions.map(
                  (details) => (
                    <Tr>
                      <Td>{details.transfers[0].account}</Td>
                      <Td isNumeric>{details.transfers[0].amount}</Td>
                      <Td>{details.charged_tx_fee}</Td>
                      <Td>
                        <Link
                          color="teal"
                          target="_blank"
                          href={`${process.env.REACT_APP_HBAR_TRANSACTION_REFFERENCE}transactions/${details.transaction_id}`}
                        >
                          {details.transaction_id}
                        </Link>
                      </Td>
                    </Tr>
                  )
                )}
              </Tbody>
            </Table>
          </TableContainer>
        </Box>
      </Container>
    </>
  );
};

export default TransactionRoute;
