import { useEffect, useContext, useState } from "react";
import {
  Box,
  Heading,
  ButtonGroup,
  Text,
  Button,
  Container,
  useToast,
  Code,
  Divider,
  Input,
  FormControl,
  InputLeftElement,
  InputGroup,
  FormHelperText,
  Alert,
  AlertIcon,
  SimpleGrid,
} from "@chakra-ui/react";
import useScriptsStore from "../../store/scripts";
import { ShopContext } from "../../context";
import { INTERNAL_SERVER_ERROR } from "../../constants/strings";
import NavBar from "../../components/navbar";
import { Form, useFormik } from "formik";
import * as Yup from "yup";

import WAValidator from "multicoin-address-validator";

const SettingsRoute = () => {
  const shop = useContext(ShopContext);
  const scripts = useScriptsStore((state) => state.scripts);
  const postScripts = useScriptsStore((state) => state.postScripts);
  const getScripts = useScriptsStore((state) => state.getScripts);
  const destroyScripts = useScriptsStore((state) => state.destroyScripts);
  const toast = useToast();

  useEffect(() => {
    getScripts(shop);
  }, []);

  const enableWidget = async () => {
    try {
      await postScripts(shop);
      toast({
        title: `Widget added successfully! Please visit your online store after 30 seconds to check the widget.`,
        status: "success",
      });
      getScripts(shop);
    } catch (e) {
      toast({
        title: e.message || INTERNAL_SERVER_ERROR,
        status: "error",
      });
    }
  };

  const walletSchema = Yup.object().shape({
    walletAddress: Yup.string().required("Wallet Address Is required"),
  });

  const XrpAddressInput = () => {
    const formik = useFormik({
      initialValues: { walletAddress: "" },
      validationSchema: walletSchema,
      onSubmit: (values) => {
        const valid = WAValidator.validate(values.walletAddress, "ripple");
        if (valid === true) {
          toast({
            title: "Wallet Address Is Valid",
            status: "success",
          });
        } else {
          toast({
            title: "Wallet Address is Invalid",
            status: "error",
          });
        }
      },
    });
    return (
      <Box>
        <FormControl onSubmit={formik.handleSubmit}>
          <Input
            id="walletAddress"
            name="walletAddress"
            type="text"
            placeholder="XRP Wallet Address"
            onChange={formik.handleChange}
            value={formik.values.walletAddress}
          />
          <FormHelperText size="sm">
            {formik.touched.walletAddress && formik.errors.walletAddress
              ? formik.errors.walletAddress
              : "Please add XRP wallet address where to receive XRP from customer"}
          </FormHelperText>
        </FormControl>

        <Button
          mt={4}
          onClick={formik.handleSubmit}
          type="submit"
          size="sm"
          colorScheme={"messenger"}
        >
          Submit
        </Button>
      </Box>
    );
  };

  const disableWidget = async () => {
    try {
      await destroyScripts(shop);
      toast({
        title: `Widget removed successfully!`,
        status: "success",
      });
      getScripts(shop);
    } catch (e) {
      toast({
        title: e.message || INTERNAL_SERVER_ERROR,
        status: "error",
      });
    }
  };

  const renderButton = () => {
    if (scripts.get.loading) {
      return (
        <Button colorScheme="gray" isLoading isDisabled>
          Loading ...
        </Button>
      );
    } else if (scripts.get.success.data.length) {
      return (
        <Button
          isLoading={scripts.destroy.loading || scripts.get.loading}
          fontWeight="bold"
          size="sm"
          colorScheme="red"
          onClick={disableWidget}
        >
          Remove Widget From Your Store
        </Button>
      );
    } else {
      return (
        <Button
          isLoading={scripts.post.loading || scripts.get.loading}
          fontWeight="bold"
          size="sm"
          colorScheme="green"
          onClick={enableWidget}
        >
          Add Widget To Your Store
        </Button>
      );
    }
  };

  return (
    <>
      <NavBar />
      <Container maxW={"7xl"} p={[12, 6]} bg="#f6f6f7" textAlign={"left"}>
        <Box as="section">
          <Box
            maxW="3xl"
            mx="auto"
            px={{ base: "6", lg: "8" }}
            py={{ base: "6", sm: "8" }}
            textAlign="left"
            borderRadius="10px"
            bg="gray.100"
          >
            {XrpAddressInput()}
          </Box>
          <br />
          <Divider />
          <Box
            maxW="3xl"
            mx="auto"
            px={{ base: "6", lg: "8" }}
            py={{ base: "6", sm: "8" }}
            textAlign="left"
          >
            <Text size="xl">Widget Embed Settings</Text>
            <Text mt="4" fontSize="sm">
              Enable or disable "Shop the look" widget on your store. The widget
              gets appended to the bottom of your store page above the footer on
              the home page.
            </Text>
            <ButtonGroup mt="4" spacing="6">
              {renderButton()}
            </ButtonGroup>

            <br />
            <br />

            <Alert status="info">
              <AlertIcon />

              <SimpleGrid>
                <Box>
                  <Text fontSize="sm">
                    NOTE: If you want the widget only on certain pages or only
                    in certain positions please add the following html tag to
                    custom liquid or custom html section.
                  </Text>
                </Box>
                <Box>
                  <Code
                    children={`<div id="frangout-shop-look-app"> </div>`}
                  ></Code>
                </Box>
              </SimpleGrid>
            </Alert>
          </Box>
        </Box>

        <Box as="section" maxW="3xl"mx="auto">
          <SimpleGrid spacing={5}>
            <Box bg="white" borderRadius={10} height='80px' boxShadow='md'>
            {/* {XrpAddressInput()} */}
            </Box>
            <Box bg="white" borderRadius={10} height='80px' boxShadow='md'>

            </Box>
          </SimpleGrid>
        </Box>
      </Container>
    </>
  );
};

export default SettingsRoute;
